import React from "react";
function Items(props){
  function CloseModal(){
    document.getElementById("myModal").style.display = "none";
  }
  function OpenModel(){
    document.getElementById("protfolioID").style.display="none";
    document.getElementById("myModal").style.display = "block";
  }
    return(
        
          
          <div className="col-lg-4 col-md-6 portfolio-item filter-app">
            <div className="portfolio-wrap">
            <div id="protfolioID">
            <img  src={props.picPath} className="img-fluid" alt=""/>
            </div>
                
                <div  className="portfolio-links ">
     
                  <div onClick={OpenModel} data-gallery="portfolioGallery" className="portfolio-lightbox" title={props.picCatNo}><i class="bi bi-plus"></i>
                     <div id="myModal" className="modal">
                     <span className="close cursor" onClick={CloseModal}>&times;</span>
                     <div className="modal-content">

                        
                          
                              <img src={props.picPath} style={{width:"100%"}}/>
                         
                      </div>
                     </div>
                  
                  </div>
                  <a href="portfolio-details.html" title="More Details"><i class="bi bi-link"></i></a>
                </div>
                <div class="portfolio-info">
                  <h4>{props.picCatNo}</h4>
                  <p>{props.picCat}</p>
                </div>
              </div>
          </div>
        
        
         
        
        
    );
}
export default Items;
