import React from "react";
import { Navigation, Pagination } from 'swiper';
// Direct React component imports
import { Swiper, SwiperSlide } from 'swiper/react';

// Styles must use direct files imports
import 'swiper/swiper-bundle.min.css'
import 'swiper/swiper.min.css'
 
function Client (){
    const swiper = new Swiper('.swiper', {
        // Optional parameters
        direction: 'vertical',
        loop: true,
      
        // If we need pagination
        pagination: {
          el: '.swiper-pagination',
        },
      
        // Navigation arrows
        navigation: {
          nextEl: '.swiper-button-next',
          prevEl: '.swiper-button-prev',
        },
      
        // And if we need scrollbar
        scrollbar: {
          el: '.swiper-scrollbar',
        },
      });

    return(
        <section id="clients" class="clients section-bg">
        <div class="container" data-aos="fade-up">
  
          <div class="section-title">
            <h2>Clients</h2>
            <p>They trusted us</p>
          </div>
  
          <div class="clients-slider swiper" data-aos="fade-up" data-aos-delay="100">
            <div class="swiper-wrapper align-items-center">
            <Swiper
            modules={[Navigation, Pagination]}
                spaceBetween={10}
              slidesPerView={7}
              pagination={{ clickable: true }}
            //    centeredSlides
               onSlideChange={() => console.log("slide change")}
                  onSwiper={swiper => console.log(swiper)}
             >
              <SwiperSlide class="swiper-slide"><img src="img/clients/client-1.png" class="img-fluid" alt=""/></SwiperSlide>
              <SwiperSlide class="swiper-slide"><img src="img/clients/client-2.png" class="img-fluid" alt=""/></SwiperSlide>
              <SwiperSlide class="swiper-slide"><img src="img/clients/client-3.png" class="img-fluid" alt=""/></SwiperSlide>
              <SwiperSlide class="swiper-slide"><img src="img/clients/client-4.png" class="img-fluid" alt=""/></SwiperSlide>
              <SwiperSlide class="swiper-slide"><img src="img/clients/client-5.png" class="img-fluid" alt=""/></SwiperSlide>
              <SwiperSlide class="swiper-slide"><img src="img/clients/client-6.png" class="img-fluid" alt=""/></SwiperSlide>
              <SwiperSlide class="swiper-slide"><img src="img/clients/client-7.png" class="img-fluid" alt=""/></SwiperSlide>
              <SwiperSlide class="swiper-slide"><img src="img/clients/client-8.png" class="img-fluid" alt=""/></SwiperSlide>
              </Swiper>
            </div>
            <div class="swiper-pagination"></div>
          </div>
  
        </div>
      </section>
    );
}
export default Client;
