import React from "react";
function Items(props){
  function CloseModal(){
    document.getElementById("myModal").style.display = "none";
  }
  function OpenModel(){
    document.getElementById("myModal").style.display = "block";
  }
    return(
        
          
          <div class="col-lg-4 col-md-6 portfolio-item filter-app">
            <div class="portfolio-wrap">
                <img src={props.picPath} class="img-fluid" alt=""/>
                <div  class="portfolio-links ">
     
                  <div onClick={OpenModel} data-gallery="portfolioGallery" class="portfolio-lightbox" title={props.picCatNo}><i class="bi bi-plus"></i>
                     <div id="myModal" class="modal">
                     <span class="close cursor" onclick={CloseModal}>&times;</span>
                     <div class="modal-content">

                        <div class="mySlides">
                          
                              <img src={props.picPath} style="width:100%"/>
                         </div>
                      </div>
                     </div>
                  
                  </div>
                  <a href="portfolio-details.html" title="More Details"><i class="bi bi-link"></i></a>
                </div>
                <div class="portfolio-info">
                  <h4>{props.picCatNo}</h4>
                  <p>{props.picCat}</p>
                </div>
              </div>
          </div>
        
        
         
        
        
    );
}
export default Items;
