import React from "react";
function Items(props){
  const lightboxStyle={
    width:"100%",
    position: "absoute"
  }
  function OpenModal(){
    document.getElementById("myModal").style.display="block";
  }
    return(
        
          
          <div class="col-lg-4 col-md-6 portfolio-item filter-app">
            <div class="portfolio-wrap">
                <img src={props.picPath} class="img-fluid" alt=""/>
                <div class="portfolio-links">
                  <a href={props.picPath} data-gallery="portfolioGallery" class="portfolio-lightbox" title={props.picCatNo}><i class="bi bi-plus"></i></a>
                  <a href="javascript:void(0)" onClick={OpenModal}  title="More Details"><i class="bi bi-link"></i></a>
                </div>

                <div id="myModal" class="modal">
                    <span class="close cursor" onclick="closeModal()">&times;</span>
                    <div class="modal-content">

                      <div class="mySlides">
                           <img src={props.picPath} style={lightboxStyle}/>
                      </div>
                    </div>
                 </div>

                <div class="portfolio-info">
                  <h4>{props.picCatNo}</h4>
                  <p>{props.picCat}</p>
                </div>
              </div>
          </div>
        
        
         
        
        
    );
}
export default Items;
