import React from "react";
import PicItems from "./portfolio-item";
import PicData from "../picData";
import List from "./portfollio-list";
import SubHeading from "./subheading";
function Portfolio (){
  const [isLightboxOpen, setLightBoxStatus]=React.useState(false);
  function UpdateStatus(){
        setLightBoxStatus(true);
  }
  function OpenModal(){
    document.getElementById("myModal").style.display="block";
  }
    function CreatePic(picDetails) {
        return (
          <PicItems
            key={picDetails.id}
            picPath={picDetails.picPath}
            picCatNo={picDetails.picCatNo}
            picCat={picDetails.picCat}
            OpenModal={OpenModal}
            UpdateStatus={UpdateStatus}
          />
        );
      }
      
    return(
        <section id="portfolio" class="portfolio">
            <div class="container" data-aos="fade-up">
  
                <SubHeading h2="Portfolio" p="Check out our beautifull portfolio"/> 

                    <div class="row" data-aos="fade-up" data-aos-delay="100">
                        <div class="col-lg-12">
                          <List />
                        </div>
                    </div>
  
                <div class="row portfolio-container" data-aos="fade-up" data-aos-delay="200">
                {/* Mapping through the PicData and return to the <PicItems/>  */}
                    <div style={{display:isLightboxOpen? "none":"block"}}>
                         {PicData.map(CreatePic)}
                    </div>
                    

                    <div id="myModal" class="modal" style={{display:isLightboxOpen? "block":"none"}} >
                    <span class="close cursor" onclick="closeModal()">&times;</span>
                    <div class="modal-content">

                      <div class="mySlides">
                           <img src="img/portfolio/portfolio-1.jpg" style={{width:"100%" }}/>
                      </div>
                    </div>
                 </div>

                </div>
  
            </div>
      </section>
    );
}
export default Portfolio;
